declare module "*.svg" {
  import React = require("react");
  export const ReactComponent: React.SFC<React.SVGProps<SVGSVGElement>>;
  const src: string;
  export default src;
}

type WebSocketConnection = "loading" | "success" | "closed";
type WebSocketTransition = "init" | "didClose" | "didOpen" | null;
